import React, { useState, useEffect } from "react";
import "./App.css";

import Start from "./components/Start";
import Question from "./components/Question";
import Result1 from "./components/Result1";
import Result2 from "./components/Result2";
import quizData from "./data/data.json";

function App() {
  const [step, setStep] = useState(1);
  const [activeQuestion, setActiveQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);

  const quizStartHandler = () => {
    setStep(2);
  };

  function NS(answers) {
    let counts = 0;
    answers.map((answer, i) => {
      if ((i == 0 || i == 3 || i == 10) && Object.values(answer)[0] == 1) {
        counts = counts + 1;
      }
    });
    if (counts < 2) return 1;
    else return 0;
  }

  function FT(answers) {
    let counts = 0;
    answers.map((answer, i) => {
      if ((i == 1 || i == 4 || i == 6) && Object.values(answer)[0] == 1) {
        counts = counts + 1;
      }
    });
    if (counts < 2) return 1;
    else return 0;
  }

  function EI(answers) {
    let counts = 0;
    answers.map((answer, i) => {
      if ((i == 2 || i == 5 || i == 8) && Object.values(answer)[0] == 1) {
        counts = counts + 1;
      }
    });
    if (counts < 2) return 1;
    else return 0;
  }

  function PJ(answers) {
    let counts = 0;
    answers.map((answer, i) => {
      if ((i == 7 || i == 9 || i == 11) && Object.values(answer)[0] == 1) {
        counts = counts + 1;
      }
    });
    if (counts < 2) return 1;
    else return 0;
  }

  return (
    <div>
      {step === 1 && <Start onQuizStart={quizStartHandler} />}
      {step === 2 && (
        <Question
          EI={EI}
          NS={NS}
          FT={FT}
          PJ={PJ}
          data={quizData.data[activeQuestion]}
          onAnswerUpdate={setAnswers}
          numberOfQuestions={quizData.data.length}
          activeQuestion={activeQuestion}
          onSetActiveQuestion={setActiveQuestion}
          onSetStep={setStep}
        />
      )}

      {step === 3 && <Result1 />}
      {step === 4 && <Result2 />}
    </div>
  );
}

export default App;
