import React, { useState, useEffect, useRef } from "react";
import { Button as GrommetButton, Box } from "grommet";
// import { animated } from "react-spring";

const Question = ({
  EI,
  NS,
  FT,
  PJ,
  data,
  answers,
  onAnswerUpdate,
  numberOfQuestions,
  activeQuestion,
  onSetActiveQuestion,
  onSetStep,
}) => {
  const nextClickHandler = (i) => {
    onAnswerUpdate((prevState) => [...prevState, { i }]);
    console.log({ activeQuestion });
    if (activeQuestion < numberOfQuestions) {
      if (activeQuestion === 11) {
        console.log({ activeQuestion });
        console.log({ answers });
        if (EI(answers)) {
          if (NS(answers)) {
            if (FT(answers)) {
              if (PJ(answers)) {
                onSetStep(3);
              } else {
                onSetStep(4);
              }
            } else {
              if (PJ(answers)) {
                onSetStep(5);
              } else {
                onSetStep(6);
              }
            }
          } else {
            if (FT(answers)) {
              if (PJ(answers)) {
                onSetStep(7);
              } else {
                onSetStep(8);
              }
            } else {
              if (PJ(answers)) {
                onSetStep(9);
              } else {
                onSetStep(10);
              }
            }
          }
        } else {
          if (NS(answers)) {
            if (FT(answers)) {
              if (PJ(answers)) {
                onSetStep(11);
              } else {
                onSetStep(12);
              }
            } else {
              if (PJ(answers)) {
                onSetStep(13);
              } else {
                onSetStep(14);
              }
            }
          } else {
            if (FT(answers)) {
              if (PJ(answers)) {
                onSetStep(15);
              } else {
                onSetStep(16);
              }
            } else {
              if (PJ(answers)) {
                onSetStep(17);
              } else {
                onSetStep(18);
              }
            }
          }
        }
      } else {
        onSetActiveQuestion(activeQuestion + 1);
      }
    }
  };

  return (
    <div className="start">
      <p className="logo">a prime</p>
      {data.question.split("n").map((line) => {
        return (
          <span className="question">
            {line}
            <br />
            <br />
          </span>
        );
      })}
      <p>{onAnswerUpdate}</p>
      {data.choices.map((choice, i) => (
        <ul>
          <button className="button" onClick={() => nextClickHandler(i)}>
            {choice}
          </button>
        </ul>
      ))}

      <footer className="footer">{data.footer}</footer>
    </div>
  );
};

export default Question;
