import React, { useState, useEffect, useRef } from "react";
import { Box, Button as GrommetButton, Grommet } from "grommet";
import "../App.css";

const Loading = ({ setStep }, Answers) => {
  function NS(answers) {
    let counts = 0;
    // for (let i = 0; i < answers.length; i++) {
    //   if (i === 0 || i === 3 || i === 10) {
    //     counts = counts + 1;
    //   }
    // }
    Answers.map((answer, i) => {
      if ((i === 0 || i === 3 || i === 10) && answer === 1) {
        counts = counts + 1;
      }
    });
    if (counts < 2) return 1;
    else return 0;
  }

  function FT(answers) {
    let counts = 0;
    // for (let i = 0; i < answers.length; i++) {
    //   if (i === 1 || i === 4 || i === 6) {
    //     counts = counts + 1;
    //   }
    // }
    Answers.map((answer, i) => {
      if ((i === 1 || i === 4 || i === 6) && answer === 1) {
        counts = counts + 1;
      }
    });
    if (counts < 2) return 1;
    else return 0;
  }

  function EI(answers) {
    let counts = 0;
    // for (let i = 0; i < answers.length; i++) {
    //   if (i === 2 || i === 5 || i === 8) {
    //     counts = counts + 1;
    //   }
    // }
    Answers.map((answer, i) => {
      if ((i === 2 || i === 5 || i === 8) && answer === 1) {
        counts = counts + 1;
      }
    });
    if (counts < 2) return 1;
    else return 0;
  }

  function PJ(answers) {
    let counts = 0;
    // for (let i = 0; i < answers.length; i++) {
    //   if (i === 7 || i === 9 || i === 11) {
    //     counts = counts + 1;
    //   }
    // }
    Answers.map((answer, i) => {
      if ((i === 7 || i === 9 || i === 11) && answer === 1) {
        counts = counts + 1;
      }
    });
    console.log(counts);
    if (counts < 2) return 1;
    else return 0;
  }
  const quizFinishHandler = () => {
    console.log({ answers });
    if (EI(answers)) {
      if (NS(answers)) {
        if (FT(answers)) {
          if (PJ(answers)) {
            setStep(3);
          } else {
            setStep(4);
          }
        } else {
          if (PJ(answers)) {
            setStep(5);
          } else {
            setStep(6);
          }
        }
      } else {
        if (FT(answers)) {
          if (PJ(answers)) {
            setStep(7);
          } else {
            setStep(8);
          }
        } else {
          if (PJ(answers)) {
            setStep(9);
          } else {
            setStep(10);
          }
        }
      }
    } else {
      if (NS(answers)) {
        if (FT(answers)) {
          if (PJ(answers)) {
            setStep(11);
          } else {
            setStep(12);
          }
        } else {
          if (PJ(answers)) {
            setStep(13);
          } else {
            setStep(14);
          }
        }
      } else {
        if (FT(answers)) {
          if (PJ(answers)) {
            setStep(15);
          } else {
            setStep(16);
          }
        } else {
          if (PJ(answers)) {
            setStep(17);
          } else {
            setStep(18);
          }
        }
      }
    }
  };
  return (
    <div className="start">
      <button className="start_button" onClick={quizFinishHandler}>
        결과 확인
      </button>
    </div>
  );
};

export default Loading;
